import React, { useState } from 'react';
import './Sidebar.css';

// 自定义组件
const Counter = ({name='',submenu1='',submenu2='',submenu3='',menu=''}) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
    console.log(isOpen);
  };
  if(menu==='true'){
  return (
    <div>
      <div className={`submenu ${isOpen ? 'open' : 'close'}`} onClick={handleToggle}>
        {name}
        <ul>
        <li>{submenu1}</li>
        <li>{submenu2}</li>
        <li>{submenu3}</li>
        </ul>
      </div>
    </div>
  );
  }else if(menu==='false'){
    <div>
        <div className='submenu'>
        {name}
        <p>Battle!!
        </p>
        </div>
    </div>
  }else{
    <div>
        <div>
        <p>menu數值有錯!!</p>
        </div>
    </div>
  }
};

// 主组件
const Sidebar = () => {
  return (
    <div className='sidebar'>
      <p>設備管理系統</p>
      <Counter name='欄位一' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位二' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位三' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位四' submenu1='一號' submenu2='二號' submenu3='三號' menu='false'/>
      <Counter name='欄位五' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
    </div>
  );
};

export default Sidebar;
