import React, { useState } from 'react';
import './Sidebar.css';

// 自定义组件
const Counter = ({name}) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
    console.log(isOpen);
  };

  return (
    <div>
      <button onClick={handleToggle}>Toggle</button>
      <div className={`my-element ${isOpen ? 'open' : ''}`}>
        {name}
      </div>
    </div>
  );
};

// 主组件
const Sidebar = () => {
  return (
    <div class='sidebar'>
      <p>設備管理系統</p>
      <Counter name='欄位一' />
      <Counter name='欄位二' />
      <Counter name='欄位三' />
    </div>
  );
};

export default Sidebar;
