import React, { useState } from 'react';
import './Sidebar.css';
import pic from './unitylogo.png'
import pic2 from './awslogo.png'
import question from './question.png'
import docker from './dockerlogo.png'
import java from './javalogo.png'
import leaf from './leaf.png'

// 自定义组件
const Counter = ({name='未輸入',submenu1='未輸入',submenu2='未輸入',submenu3='未輸入',menu='未輸入',picsrc=question},id='') => {
  const [isOpen, setIsOpen] = useState(false);
  const handleToggle = () => {
    setIsOpen(!isOpen);
    console.log(isOpen);
  };
  // 檢查是否有填ID
  if(id===undefined){console.log('Sidebar.js有ID未填')}
  if(menu==='true'){
    const tmp = id;
  return (
    <div>
      <div onClick={handleToggle}>
      <img src={picsrc} alt='submenu'></img>
        <span>{name}</span>
      </div>
        <ul className={`submenu ${isOpen ? 'open' : 'close'}`}>
        <li id={`${tmp}0`}>{submenu1}</li>
        <li id ={tmp+'1'}>{submenu2}</li>
        <li id ={tmp+'2'}>{submenu3}</li>
        </ul>
    </div>
  );
  }else if(menu==='false'){
    return(
        <div>
          <img src={picsrc} alt='none'></img>
        <span>{name}</span>
        </div>
    )
  }else{
    return(
    <div>
        <div>
        <p>menu數值有錯!!</p>
        </div>
    </div>
    )
  }
};

// 主组件
const Sidebar = (props) => {
  return (
    <div className={`sidebar ${props.state ? 'open' : 'close'}`}>
      <div className='title'>
        <img src={pic} alt='pic'></img>
        <p>設備管理系統</p>
      </div>
      <Counter name='欄位一' submenu1='一號' submenu2='二號' submenu3='三號' menu='true' picsrc={pic2} id = '0' />
      <Counter name='欄位二' submenu1='一號' submenu2='二號' submenu3='三號' menu='true' picsrc={docker} id = '1'/>
      <Counter name='欄位三' submenu1='一號' submenu2='二號' submenu3='三號' menu='true' picsrc={java} id = '2'/>
      <Counter name='欄位四' menu='false' picsrc={leaf} id = '3'/>
      <Counter name='欄位五' submenu1='一號' submenu2='二號' submenu3='三號' menu='true' picsrc={question} id = '4'/>
    </div>
  );
};

export default Sidebar;
