            case '資訊室':
              ITCount++;
              break;
            case '醫務室':
              medicalCount++;
              break;
            default:
              break;
          }
        }
      }
      //更新hook
      setEquipments(newDataFromBackend)
      setEquipmentsnumber({all:officeCount+ITCount+medicalCount,
        office:officeCount,
        IT:ITCount,
        medical:medicalCount,})
    });
    console.log('網頁加載完成');
  }

  const addequipment = () => {
   alert('addequipment功能已被觸發')
  }
  const editequipment = () => {
    alert('editequipment功能已被觸發')
  }
  const delequipment =() => {
    alert('delequipment功能已被觸發')
  }
  const showaddequipment = () => {
    let a= document.getElementsByClassName('addequipment');
    let b=a.style.display;
    a.style.display= !b;
  }


  return (
    <div className={`main ${props.state ? '' : 'close'}`}>
      <div className='infomation'>
      <h1>設備管理系統</h1>
      <p>總設備數量:{equipmentsnumber.all}  資訊室設備數量:{equipmentsnumber.IT}  醫務室設備數量:{equipmentsnumber.medical}  辦公室設備數量{equipmentsnumber.office}</p>
      <label htmlFor="dropdown">設備區域：</label>
      <select id="dropdown" value={selectedarea} onChange={handleAreaChange}>
        <option value="">請選擇</option>
        <option value="全部">全部</option>
        <option value="資訊室">資訊室</option>
        <option value="醫務室">醫務室</option>
        <option value="辦公室">辦公室</option>
      </select>
      <p>所在區域: {selectedarea}</p>
      <button onClick={logout}>登出</button>
      </div>

      <div className='addequipment'>
      <form onSubmit={addequipment}>
      <label>設備名稱:</label>
      <input value={name} onChange={(e)=>setName(e.target.value)}style={{ width: '150px' }} placeholder='不要超過9個字喔'/>
      <label>擁有人:</label>
      <input value={owner} onChange={(e)=>setOwner(e.target.value)}style={{ width: '150px' }} placeholder='只能輸入onwer拉'/>
      <label for="position">地點:</label>
        <select onChange={(e)=>setPosition(e.target.value)}>
            <option value="資訊室">資訊室</option>
            <option value="醫務室">醫務室</option>
            <option value="辦公室">辦公室</option>
        </select>
      <label for="model">型號:</label>
        <select onChange={(e)=>setModel(e.target.value)}>
            <option value='Apple'>Apple</option>
            <option value='TSMC'>TSMC</option>
            <option value='Google'>Google</option>
        </select>
      <label>財產編號:</label>
      <input value={code} onChange={(e)=>setCode(e.target.value)}style={{ width: '150px' }} />
      <label>年份:</label>
      <select onChange={(e)=>setYear(e.target.value)}>
            <option value='2021'>2021</option>
            <option value='2022'>2022</option>
            <option value='2023'>2023</option>
        </select>
      <input type='submit' value="建立"></input>
      </form>
      </div>

      <div className='Cardstitle'><h2>{selectedarea}</h2>
      <button className='button1' onClick={addequipment}>新增設備</button>
      <button  onClick={editequipment}>修改設備</button>
      <button  onClick={delequipment}>刪除設備</button>
      <button  onClick={showmore}>顯示更多</button>
      </div>
      <div className='Cards'>

      {equipments.slice(0, visibleDevices).map((equipment, index) => (
        selectedarea === equipment.position || selectedarea ==='全部' ?(
          <Card
            key={index}
            name={equipment.name}
            owner={equipment.principal}
            position={equipment.position}
            model={equipment.model}
            code={equipment.code}
            date={equipment.year}
            category={equipment.category}
          />
        ) : null
      ))}
      </div>
    </div>
  );
};

export default Twoandtwomainpage;
