import React, { useState } from 'react';
import './Sidebar.css';
import pic from './unitylogo.png'
import pic2 from './awslogo.png'
import question from './question.png'

// 自定义组件
const Counter = ({name='未輸入',submenu1='未輸入',submenu2='未輸入',submenu3='未輸入',menu='未輸入',showpic=false,picsrc=question}) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
    console.log(isOpen);
  };
  if(menu==='true'){
  return (
    <div>
      <div onClick={handleToggle}>
      <img src={showpic ? picsrc:picsrc} alt='pic2'></img>
        <span>{name}</span>
      </div>
        <ul className={`submenu ${isOpen ? 'open' : 'close'}`}>
        <li>{submenu1}</li>
        <li>{submenu2}</li>
        <li>{submenu3}</li>
        </ul>
    </div>
  );
  }else if(menu==='false'){
    return(
        <div className='submenu'>
        <span>{name}</span>
        </div>
    )
  }else{
    return(
    <div>
        <div>
        <p>menu數值有錯!!</p>
        </div>
    </div>
    )
  }
};

// 主组件
const Sidebar = (props) => {
  return (
    <div className={`sidebar ${props.state ? 'open' : 'close'}`}>
      <div className='title'>
        <img src={pic} alt='pic'></img>
        <p>設備管理系統</p>
      </div>
      <Counter name='欄位一' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位二' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位三' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
      <Counter name='欄位四' menu='false'/>
      <Counter name='欄位五' submenu1='一號' submenu2='二號' submenu3='三號' menu='true'/>
    </div>
  );
};

export default Sidebar;
